mapper.select:
SELECT
r.id ,
r.user_id,
u.account,
u.phone,
r.race_id,
r.bet_type,
r.bet_content as content,
r.bet_result,
r.create_time,
r.rebate_status
FROM t_bet_recd r
LEFT JOIN t_user_info u ON u.user_id=r.user_id


        r.id=#{betId}


        AND u.phone=#{phone}


        AND u.account=#{account}


        AND r.race_id=#{raceId}


        AND r.bet_content->'$.score'=#{score}


        AND r.bet_type=#{betType}


        AND r.bet_result=#{betResult}


        AND r.rebate_status=#{rebateStatus}


        AND r.create_time >=#{startTime}


        AND r.create_time <=#{endTime}


order by create_time desc

select
sum(r.bet_amount) as bet_amount,
sum(if(r.bet_result=0,bet_amount,0)) as bet_lose_amount,
sum(if(r.bet_result=1,r.bet_content->'$.expecAmount',0)) as bet_win_amount,
sum(if(r.bet_result=1,r.bet_content->'$.expecAmount',0)) - sum(r.bet_amount) as balance_amount,
count(0) as count_num
FROM t_bet_recd r
LEFT JOIN t_user_info u ON u.user_id=r.user_id


        r.id=#{betId}


        AND u.phone=#{phone}


        AND u.account=#{account}


        AND r.race_id=#{raceId}


        AND r.bet_content->'$.score'=#{score}


        AND r.bet_type=#{betType}


        AND r.bet_result=#{betResult}


        AND r.rebate_status=#{rebateStatus}


        AND r.create_time >=#{startTime}


        AND r.create_time <=#{endTime}

select
sum(r.bet_amount) as bet_amount,
sum(if(r.bet_result=0,bet_amount,0)) as bet_lose_amount,
sum(if(r.bet_result=1,r.bet_content->'$.expecAmount',0)) as bet_win_amount,
sum(if(r.bet_result=1,r.bet_content->'$.expecAmount',0)) - sum(r.bet_amount) as balance_amount,
count(0) as count_num
FROM t_bet_recd r
LEFT JOIN t_user_info u ON u.user_id=r.user_id


        r.user_id=#{userId}


        and r.bet_type=#{type}


        AND r.create_time >=#{startTime}


        AND r.create_time <=#{endTime}

select rebate_id, sum(bet_amount) as bet_amount, count(id) as bet_count, sum(if(bet_result=1,bet_content->'$.expecAmount',0)) as bet_win_amount, sum(if(bet_result=0,bet_amount,0)) as bet_lose_amount, sum(bet_amount)-sum(if(bet_result=1,bet_content->'$.expecAmount',0)) as balance_amount, cast(avg(bet_content->'$.rebateRatio') as decimal(10,2)) as avg_rebate_ratio from t_bet_recd where bet_status = 1 and rebate_id in
#{rebateId}
group by rebate_id
